import React, { useState, useEffect, useCallback, createContext, useMemo } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Footer from './components/layout/Footer';
import Home from './components/pages/Home';
import Collection from './components/pages/Collection';
import ProductDetail from './components/pages/ProductDetail';
import FeedbackForm from './components/pages/FeedbackForm';
import ContactForm from './components/pages/ContactForm';
import AboutUs from './components/pages/AboutUs';
import Search from './components/pages/Search';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import CartPage from './components/pages/CartPage';
import OrdersPage from './components/pages/OrdersPage';
import PaymentSuccess from './components/pages/PaymentSuccess';
import PaymentCancel from './components/pages/PaymentCancel';

import AdminLogin from './components/admin/AdminLogin';
import AdminDashboard from './components/admin/AdminDashboard';
import AdminProtectedRoute from './components/admin/AdminProtectedRoute';
import ScrollToTop from './services/ScrollToTop';
import CartService from './services/CartService';
import './styles/App.css';
import './styles/Admin.css';

// Create cart context for global state management
export const CartContext = createContext();

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const location = useLocation();

  // Check if current route is an admin route
  const isAdminRoute = location.pathname.startsWith('/admin');

  // Load user from localStorage on component mount
  useEffect(() => {
    const user = localStorage.getItem('currentUser');
    if (user) {
      try {
        const userData = JSON.parse(user);
        setCurrentUser(userData);
        console.log('User loaded from localStorage:', userData);
      } catch (error) {
        console.error('Error parsing user data from localStorage:', error);
        localStorage.removeItem('currentUser'); // Clear invalid data
      }
    }
  }, []);

  // Function to fetch cart items (will be called only when needed, not automatically)
  // Wrapped in useCallback to prevent unnecessary re-renders
  const fetchCartItems = useCallback(async () => {
    if (currentUser && currentUser.customerId) {
      try {
        console.log('Fetching cart items for user:', currentUser.customerId);
        const items = await CartService.getCartItems(currentUser.customerId);
        setCartItems(items.map(item => ({
          ...item,
          id: item.id,
          name: item.product.name,
          price: item.product.price,
          image: item.product.image,
          selected: false,
          quantity: item.quantity
        })));
      } catch (error) {
        console.error('Failed to fetch cart items:', error);
      }
    } else {
      // If no user, clear the cart
      setCartItems([]);
    }
  }, [currentUser, setCartItems]);

  // Automatically fetch cart items when the user logs in or the app loads
  useEffect(() => {
    fetchCartItems();
  }, [fetchCartItems]);

  // We're no longer automatically fetching cart items when user changes
  // This prevents unnecessary API calls on page load

  // Cart functions
  const addToCart = useCallback(async (product) => {
    if (!currentUser) {
      alert('Please login to add items to your cart');
      return;
    }
    const customerId = currentUser.customerId;
    if (!customerId) {
      console.error('No customer ID found for user:', currentUser);
      alert('Customer profile not found. Please try logging in again.');
      return;
    }
    try {
      await CartService.addToCart(customerId, product.id, 1);
      await fetchCartItems(); // Refetch to ensure consistency
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  }, [currentUser, fetchCartItems]);

  const removeFromCart = useCallback(async (id) => {
    if (!currentUser || !currentUser.customerId) return;
    try {
      await CartService.removeFromCart(id);
      setCartItems(prev => prev.filter(item => item.id !== id)); // Optimistic update
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  }, [currentUser]);

  const updateQuantity = useCallback(async (id, delta) => {
    if (!currentUser || !currentUser.customerId) return;

    const item = cartItems.find(item => item.id === id);
    if (!item) return;

    const newQuantity = Math.max(1, item.quantity + delta);

    try {
      // Optimistic update
      setCartItems(prev =>
        prev.map(i =>
          i.id === id
            ? { ...i, quantity: newQuantity }
            : i
        )
      );
      await CartService.updateCartItemQuantity(id, newQuantity);
    } catch (error) {
      console.error('Error updating quantity:', error);
      await fetchCartItems(); // Revert on error
    }
  }, [currentUser, cartItems, fetchCartItems]);

  const toggleSelected = useCallback((id) => {
    setCartItems(prev =>
      prev.map(item =>
        item.id === id ? { ...item, selected: !item.selected } : item
      )
    );
  }, []);

  // Memoize the context value to prevent unnecessary re-renders of consumers
  const contextValue = useMemo(() => ({
    cartItems,
    addToCart,
    removeFromCart,
    updateQuantity,
    toggleSelected,
    setCartItems,
    currentUser,
    setCurrentUser
  }), [cartItems, currentUser, addToCart, removeFromCart, updateQuantity, toggleSelected]);

  return (
    <CartContext.Provider value={contextValue}>
      <div className="app">
        {!isAdminRoute && <Navbar />}
        <ScrollToTop />
        <main className={isAdminRoute ? "admin-main-content" : "main-content"}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/collection" element={<Collection />} />
            <Route path="/product/:id" element={<ProductDetail />} />
            <Route path="/feedback" element={<FeedbackForm />} />
            <Route path="/contact" element={<ContactForm />} />
            <Route path="/about" element={<AboutUs />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/cart" element={<CartPage />} />
            <Route path="/orders" element={<OrdersPage />} />
            <Route path="/search" element={<Search />} />
            <Route path="/payment-success" element={<PaymentSuccess />} />
            <Route path="/payment-cancel" element={<PaymentCancel />} />

            {/* Admin Routes */}
            <Route path="/admin/login" element={<AdminLogin />} />
            <Route element={<AdminProtectedRoute />}>
              <Route path="/admin/dashboard" element={<AdminDashboard />} />
              <Route path="/admin/orders" element={<AdminDashboard />} />
            </Route>
          </Routes>
        </main>
        {!isAdminRoute && <Footer />}
      </div>
    </CartContext.Provider>
  );
}

export default App;
