import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart, faSpinner, faExclamationTriangle } from '@fortawesome/free-solid-svg-icons';
import '../../styles/AdminOrdersPage.css';

const AdminOrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedOrderId, setExpandedOrderId] = useState(null);

  // Status options for dropdown
  const statusOptions = [
    'PENDING',
    'PROCESSING',
    'SHIPPED',
    'DELIVERED',
    'CANCELLED'
  ];

  // Fetch all orders
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:8080/api/orders');
        setOrders(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching orders:', err);
        setError('Failed to load orders. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  // Handle status change
  const handleStatusChange = async (orderId, newStatus) => {
    try {
      // Send status as a plain string as expected by the backend
      await axios.patch(`http://localhost:8080/api/orders/${orderId}/status`, newStatus, {
        headers: {
          'Content-Type': 'text/plain'
        }
      });

      // Update local state
      setOrders(prevOrders => 
        prevOrders.map(order => 
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );

      console.log(`Order #${orderId} status updated to ${newStatus}`);
    } catch (err) {
      console.error('Error updating order status:', err);
      alert(`Failed to update order status: ${err.message}`);
    }
  };

  // Toggle order details expansion
  const toggleOrderDetails = (orderId) => {
    setExpandedOrderId(expandedOrderId === orderId ? null : orderId);
  };

  // Format date
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  // Format price
  const formatPrice = (price) => {
    if (!price) return '$0.00';
    return `$${parseFloat(price).toFixed(2)}`;
  };

  if (loading) {
    return (
      <div className="admin-orders-page">
        <h1>Order Management</h1>
        <div className="loading">
          <FontAwesomeIcon icon={faSpinner} spin /> Loading orders...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-orders-page">
        <h1>Order Management</h1>
        <div className="error">
          <FontAwesomeIcon icon={faExclamationTriangle} /> {error}
        </div>
      </div>
    );
  }

  return (
    <div className="admin-orders-page">
      <h1>
        <FontAwesomeIcon icon={faShoppingCart} /> Order Management
      </h1>

      {orders.length === 0 ? (
        <div className="no-orders">No orders found.</div>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Customer</th>
              <th>Total</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <React.Fragment key={order.id}>
                <tr className={expandedOrderId === order.id ? 'expanded' : ''}>
                  <td>{order.id}</td>
                  <td>{formatDate(order.orderDate)}</td>
                  <td>
                    {order.customer ? 
                      `${order.customer.firstName} ${order.customer.lastName}` : 
                      'Unknown Customer'}
                  </td>
                  <td>{formatPrice(order.totalAmount)}</td>
                  <td>
                    <select 
                      value={order.status}
                      onChange={(e) => handleStatusChange(order.id, e.target.value)}
                      className={`status-${order.status?.toLowerCase()}`}
                    >
                      {statusOptions.map(status => (
                        <option key={status} value={status}>{status}</option>
                      ))}
                    </select>
                  </td>
                  <td>
                    <button 
                      className="view-details-btn"
                      onClick={() => toggleOrderDetails(order.id)}
                    >
                      {expandedOrderId === order.id ? 'Hide Details' : 'View Details'}
                    </button>
                  </td>
                </tr>
                {expandedOrderId === order.id && (
                  <tr className="order-details">
                    <td colSpan="6">
                      <div className="order-details-content">
                        <h3>Order Items</h3>
                        <table className="items-table">
                          <thead>
                            <tr>
                              <th>Product</th>
                              <th>Price</th>
                              <th>Quantity</th>
                              <th>Subtotal</th>
                            </tr>
                          </thead>
                          <tbody>
                            {order.orderItems && order.orderItems.map(item => (
                              <tr key={item.id}>
                                <td>{item.product?.name || 'Unknown Product'}</td>
                                <td>{formatPrice(item.price)}</td>
                                <td>{item.quantity}</td>
                                <td>
                                  {formatPrice(item.price * item.quantity)}
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>

                        <div className="order-address">
                          <h3>Shipping Address</h3>
                          {order.shippingAddress ? (
                            <p>
                              {order.shippingAddress.street}<br />
                              {order.shippingAddress.city}, {order.shippingAddress.state} {order.shippingAddress.zipCode}<br />
                              {order.shippingAddress.country}
                            </p>
                          ) : (
                            <p>No address information available</p>
                          )}
                        </div>

                        <div className="order-payment">
                          <h3>Payment Information</h3>
                          {order.payments && order.payments.length > 0 ? (
                            <div>
                              <p><strong>Method:</strong> {order.payments[0].paymentMethod}</p>
                              <p><strong>Status:</strong> {order.payments[0].status}</p>
                              <p><strong>Transaction ID:</strong> {order.payments[0].transactionId}</p>
                              <p><strong>Date:</strong> {formatDate(order.payments[0].paymentDate)}</p>
                            </div>
                          ) : (
                            <p>No payment information available</p>
                          )}
                        </div>
                      </div>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminOrdersPage;
