import React, { useState, useEffect, useContext } from 'react';
import api from '../../services/api';
import ProductItem from '../common/ProductItem';
import { CartContext } from '../../App';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilter, faTimes, faSort, faSearch, faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';
import '../../styles/Collection.css';

const Collection = () => {
  const { addToCart } = useContext(CartContext);
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filtering and Sorting State
  const [activeCategory, setActiveCategory] = useState(null);
  const [sortOption, setSortOption] = useState('default');
  const [showFilters, setShowFilters] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Pagination State
  const [page, setPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [totalProducts, setTotalProducts] = useState(0);

  // Fetch categories (only once)
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const categoriesResponse = await api.get('/categories');
        setCategories(categoriesResponse.data);
      } catch (err) {
        console.error('Error fetching categories:', err);
      }
    };
    fetchCategories();
  }, []);

  // Fetch products based on filters, sorting, and pagination
  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      try {
        const params = {
          page,
          size: 20,
          categoryId: activeCategory,
          keyword: searchTerm || null,
        };

        if (sortOption !== 'default') {
          const [property, direction] = sortOption.split('-');
          params.sort = `${property},${direction}`;
        }

        const response = await api.get('/products', { params });
        console.log('API Response Data:', response.data); // New debugging log

        setProducts(response.data.content);
        setTotalPages(response.data.totalPages);
        setTotalProducts(response.data.totalElements);
        setError(null);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError(err.message);
        setProducts([]);
        setTotalPages(0);
        setTotalProducts(0);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [page, activeCategory, sortOption, searchTerm]);

  // Reset page to 0 when filters change
  useEffect(() => {
    setPage(0);
  }, [activeCategory, sortOption, searchTerm]);

  const handleCategoryFilter = (categoryId) => {
    setActiveCategory(activeCategory === categoryId ? null : categoryId);
  };

  const handleSort = (option) => {
    setSortOption(option);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    setPage(0); // Trigger search by resetting page, which is a dependency of the fetch effect
  };

  const handleAddToCart = (product) => {
    addToCart(product);
  };



  return (
    <div className="collection-page">
      <div className="collection-header wrapper">
        <h1 className="h1-heading">Our Collection</h1>
        <p className="avg-para">Discover our carefully curated furniture collection</p>
      </div>

      <div className="collection-filters wrapper">
        <div className="filter-controls">
          <button 
            className="filter-toggle btn outline-btn"
            onClick={() => setShowFilters(!showFilters)}
          >
            <FontAwesomeIcon icon={showFilters ? faTimes : faFilter} />
            {showFilters ? ' Hide Filters' : ' Show Filters'}
          </button>

          <div className="main-search-container collection-search">
            <form onSubmit={handleSearchSubmit} className="main-search-form">
              <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="main-search-input"
              />
              <button type="submit" className="main-search-button">
                <FontAwesomeIcon icon={faSearch} />
              </button>
            </form>
          </div>

          <div className="sort-dropdown">
            <label>
              <FontAwesomeIcon icon={faSort} /> Sort by:
              <select value={sortOption} onChange={(e) => handleSort(e.target.value)}>
                <option value="default">Default</option>
                <option value="price-asc">Price: Low to High</option>
                <option value="price-desc">Price: High to Low</option>
                <option value="name-asc">Name: A-Z</option>
                <option value="name-desc">Name: Z-A</option>
              </select>
            </label>
          </div>
        </div>

        {showFilters && (
          <div className="category-filters">
            <h4>Categories</h4>
            <div className="category-buttons">
              {categories.map(category => (
                <button
                  key={category.id}
                  className={`category-btn ${activeCategory === category.id ? 'active' : ''}`}
                  onClick={() => handleCategoryFilter(category.id)}
                >
                  {category.name}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      <div className="collection-content wrapper">
        {loading ? (
          <div className="loading">Loading products...</div>
        ) : error ? (
          <div className="error">Error: {error}</div>
        ) : (
          <>
            <div className="products-count">
              Showing {products.length} of {totalProducts} products
              {activeCategory && (
                <button 
                  className="clear-filters"
                  onClick={() => setActiveCategory(null)}
                >
                  Clear filters
                </button>
              )}
            </div>

            <div className="products-grid">
              {products.length > 0 ? (
                products.map(product => (
                  <ProductItem 
                    key={product.id} 
                    product={product} 
                    onAddToCart={handleAddToCart} 
                  />
                ))
              ) : (
                <div className="no-products">
                  <p>No products found matching your criteria.</p>
                </div>
              )}
            </div>

            {totalPages > 1 && (
              <div className="pagination-controls">
                <button 
                  onClick={() => setPage(page - 1)} 
                  disabled={page === 0}
                  className="pagination-btn"
                >
                  <FontAwesomeIcon icon={faChevronLeft} /> Previous
                </button>
                <span className="pagination-info">
                  Page {page + 1} of {totalPages}
                </span>
                <button 
                  onClick={() => setPage(page + 1)} 
                  disabled={page >= totalPages - 1}
                  className="pagination-btn"
                >
                  Next <FontAwesomeIcon icon={faChevronRight} />
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Collection;
