import React, { useContext, useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart, faArrowLeft, faPlus } from '@fortawesome/free-solid-svg-icons';
import CartItem from '../common/CartItem';
import { CartContext } from '../../App';
import PayPalButton from '../paypal/PayPalButton';
import '../../styles/CartPage.css';
import '../../styles/PayPalButton.css';
import axios from 'axios';

const CartPage = () => {
  const { cartItems, setCartItems } = useContext(CartContext);
  const [processingOrder, setProcessingOrder] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [addresses, setAddresses] = useState([]);
  const [selectedAddressId, setSelectedAddressId] = useState('');
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('CREDIT_CARD');
  const [creditCardInfo, setCreditCardInfo] = useState({
    cardNumber: '',
    cardholderName: '',
    expiryDate: '',
    cvv: ''
  });
  const [newAddress, setNewAddress] = useState({
    street: '',
    city: '',
    state: '',
    zipCode: '',
    country: ''
  });

  const [customerId, setCustomerId] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      try {
        const userData = JSON.parse(storedUser);
        if (userData && userData.customerId) {
          setCustomerId(userData.customerId);
          console.log('Customer ID loaded from localStorage:', userData.customerId);
        } else {
          console.error('customerId not found in userData from localStorage');
          // Handle missing customerId, e.g., redirect to login or show an error
        }
      } catch (error) {
        console.error('Error parsing currentUser from localStorage:', error);
        // Handle parsing error
      }
    } else {
      console.error('currentUser not found in localStorage');
      // Handle user not logged in, e.g., redirect to login or show an error
    }
  }, []); // Empty dependency array ensures this runs once on mount

  // Calculate total price
  const calculateTotal = () => {
    return cartItems
      .filter(item => item.selected)
      .reduce((sum, item) => {
        const itemPrice = typeof item.price === 'string' 
          ? parseInt(item.price.replace(/\D/g, ''))
          : item.price;
        return sum + (item.quantity * itemPrice);
      }, 0);
  };

  const total = calculateTotal();

  // Handle select all
  const handleSelectAll = (e) => {
    const checked = e.target.checked;
    setCartItems(prev =>
      prev.map(item => ({ ...item, selected: checked }))
    );
  };

  // Are all items selected?
  const allSelected = cartItems.length > 0 && cartItems.every(item => item.selected);

  // Number of selected items
  const selectedCount = cartItems.filter(item => item.selected).length;

  // Fetch customer addresses from the backend
  const fetchAddresses = useCallback(async () => {
    if (!customerId) return; // Don't fetch if customerId isn't loaded yet
    try {
      const response = await axios.get(`http://localhost:8080/api/checkout/${customerId}/addresses`);
      setAddresses(response.data);
      if (response.data.length > 0 && !selectedAddressId) { // Also check if selectedAddressId is not already set
        setSelectedAddressId(response.data[0].id);
      }
    } catch (error) {
      console.error('Error fetching addresses:', error);
      alert('Failed to load addresses. Please try again.');
    }
  }, [customerId, selectedAddressId]); // Added selectedAddressId to prevent re-selecting if already set by user

  useEffect(() => {
    if (showCheckout && customerId) { // Ensure customerId is loaded before fetching
      fetchAddresses();
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps 
  }, [showCheckout, customerId, fetchAddresses]); // fetchAddresses is now stable due to useCallback

  // Handle address form input changes
  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setNewAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle credit card form input changes
  const handleCreditCardChange = (e) => {
    const { name, value } = e.target;
    setCreditCardInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle adding a new address
  const handleAddAddress = async (e) => {
    e.preventDefault();
    try {
      setProcessingOrder(true);
      const response = await axios.post(
        `http://localhost:8080/api/checkout/${customerId}/address`,
        newAddress
      );

      // Add the new address to the list and select it
      setAddresses(prev => [...prev, response.data]);
      setSelectedAddressId(response.data.id);

      // Reset the form and hide it
      setNewAddress({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: ''
      });
      setShowAddressForm(false);
    } catch (error) {
      console.error('Error adding address:', error);
      alert('Failed to add address. Please try again.');
    } finally {
      setProcessingOrder(false);
    }
  };

  // Handle proceeding to checkout
  const handleProceedToCheckout = () => {
    const selected = cartItems.filter(item => item.selected);
    if (selected.length === 0) {
      alert("Please select products to checkout!");
    } else {
      setShowCheckout(true);
    }
  };

  // Handle checkout
  const handleCheckout = async () => {
    // If PayPal is selected, the PayPal button will handle the checkout process
    if (paymentMethod === 'PAYPAL') {
      return;
    }

    setProcessingOrder(true);

    try {
      console.log('Sending checkout request with:', {
        customerId,
        paymentMethod
      });

      // Send the payment method along with the request
      const url = `http://localhost:8080/api/checkout/${customerId}/complete`;
      await axios.post(url, null, {
        params: {
          paymentMethod: paymentMethod
        }
      });

      alert(`Order successful! Total: $${total.toLocaleString()}`);

      // Remove checked out items from cart
      setCartItems(prev => prev.filter(item => !item.selected));
      setShowCheckout(false);
    } catch (error) {
      console.error('Error processing order:', error);
      alert('Failed to process order. Please try again.');
    } finally {
      setProcessingOrder(false);
    }
  };

  return (
    <div className="cart-page">
      <div className="cart-container">
        <div className="cart-header">
          <h1>
            <FontAwesomeIcon icon={faShoppingCart} /> Shopping Cart
          </h1>
          <Link to="/" className="continue-shopping">
            <FontAwesomeIcon icon={faArrowLeft} /> Continue Shopping
          </Link>
        </div>

        {cartItems.length === 0 ? (
          <div className="empty-cart">
            <h2>Your cart is empty</h2>
            <p>Add some items to your cart to see them here.</p>
            <Link to="/" className="btn brown-bg">Start Shopping</Link>
          </div>
        ) : showCheckout ? (
          <div className="checkout-container">
            <h2>Checkout</h2>

            <div className="checkout-section">
              <h3>Shipping Address</h3>

              {addresses.length > 0 && (
                <div className="address-selection">
                  <select 
                    value={selectedAddressId} 
                    onChange={(e) => setSelectedAddressId(e.target.value)}
                    className="address-select"
                  >
                    {addresses.map(address => (
                      <option key={address.id} value={address.id}>
                        {address.street}, {address.city}, {address.state}, {address.zipCode}, {address.country}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              {!showAddressForm ? (
                <button 
                  className="add-address-btn" 
                  onClick={() => setShowAddressForm(true)}
                >
                  <FontAwesomeIcon icon={faPlus} /> Add New Address
                </button>
              ) : (
                <form className="address-form" onSubmit={handleAddAddress}>
                  <div className="form-group">
                    <label>Street:</label>
                    <input 
                      type="text" 
                      name="street" 
                      value={newAddress.street} 
                      onChange={handleAddressChange}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>City:</label>
                    <input 
                      type="text" 
                      name="city" 
                      value={newAddress.city} 
                      onChange={handleAddressChange}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>State:</label>
                    <input 
                      type="text" 
                      name="state" 
                      value={newAddress.state} 
                      onChange={handleAddressChange}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Zip Code:</label>
                    <input 
                      type="text" 
                      name="zipCode" 
                      value={newAddress.zipCode} 
                      onChange={handleAddressChange}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Country:</label>
                    <input 
                      type="text" 
                      name="country" 
                      value={newAddress.country} 
                      onChange={handleAddressChange}
                      required
                    />
                  </div>

                  <div className="form-buttons">
                    <button type="submit" className="save-address-btn">Save Address</button>
                    <button 
                      type="button" 
                      className="cancel-btn"
                      onClick={() => setShowAddressForm(false)}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              )}
            </div>

            <div className="checkout-section">
              <h3>Payment Method</h3>
              <div className="payment-method-options">
                <div 
                  className={`payment-method-option ${paymentMethod === 'CREDIT_CARD' ? 'selected' : ''}`}
                  onClick={() => setPaymentMethod('CREDIT_CARD')}
                >
                  <input 
                    type="radio" 
                    id="credit-card" 
                    name="payment-method" 
                    value="CREDIT_CARD" 
                    checked={paymentMethod === 'CREDIT_CARD'} 
                    onChange={() => setPaymentMethod('CREDIT_CARD')} 
                  />
                  <label htmlFor="credit-card">Credit Card</label>
                </div>
                <div 
                  className={`payment-method-option ${paymentMethod === 'CASH_ON_DELIVERY' ? 'selected' : ''}`}
                  onClick={() => setPaymentMethod('CASH_ON_DELIVERY')}
                >
                  <input 
                    type="radio" 
                    id="cash-on-delivery" 
                    name="payment-method" 
                    value="CASH_ON_DELIVERY" 
                    checked={paymentMethod === 'CASH_ON_DELIVERY'} 
                    onChange={() => setPaymentMethod('CASH_ON_DELIVERY')} 
                  />
                  <label htmlFor="cash-on-delivery">Cash on Delivery</label>
                </div>
                <div 
                  className={`payment-method-option ${paymentMethod === 'PAYPAL' ? 'selected' : ''}`}
                  onClick={() => setPaymentMethod('PAYPAL')}
                >
                  <input 
                    type="radio" 
                    id="paypal" 
                    name="payment-method" 
                    value="PAYPAL" 
                    checked={paymentMethod === 'PAYPAL'} 
                    onChange={() => setPaymentMethod('PAYPAL')} 
                  />
                  <label htmlFor="paypal">PayPal</label>
                </div>
              </div>

              {/* Credit Card Form - Only show when Credit Card is selected */}
              {paymentMethod === 'CREDIT_CARD' && (
                <div className="credit-card-form">
                  <h3>Credit Card Information</h3>
                  <div className="form-group">
                    <label htmlFor="card-number">Card Number</label>
                    <input
                      type="text"
                      id="card-number"
                      name="cardNumber"
                      value={creditCardInfo.cardNumber}
                      onChange={handleCreditCardChange}
                      placeholder="1234 5678 9012 3456"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="cardholder-name">Cardholder Name</label>
                    <input
                      type="text"
                      id="cardholder-name"
                      name="cardholderName"
                      value={creditCardInfo.cardholderName}
                      onChange={handleCreditCardChange}
                      placeholder="John Doe"
                      required
                    />
                  </div>
                  <div className="form-row">
                    <div className="form-group">
                      <label htmlFor="expiry-date">Expiry Date</label>
                      <input
                        type="text"
                        id="expiry-date"
                        name="expiryDate"
                        value={creditCardInfo.expiryDate}
                        onChange={handleCreditCardChange}
                        placeholder="MM/YY"
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="cvv">CVV</label>
                      <input
                        type="text"
                        id="cvv"
                        name="cvv"
                        value={creditCardInfo.cvv}
                        onChange={handleCreditCardChange}
                        placeholder="123"
                        required
                      />
                    </div>
                  </div>
                </div>
              )}

              {paymentMethod === 'PAYPAL' && (
                <div className="paypal-container">
                  <h3>PayPal Payment</h3>
                  <p>Click the PayPal button below to complete your payment securely with PayPal.</p>
                  <PayPalButton 
                    total={total} 
                    customerId={customerId} 
                    addressId={selectedAddressId} 
                  />
                </div>
              )}
            </div>

            <div className="checkout-section">
              <h3>Order Summary</h3>
              <div className="order-items">
                {cartItems.filter(item => item.selected).map(item => (
                  <div key={item.id} className="order-item-summary">
                    <span>{item.name} x {item.quantity}</span>
                    <span>${(item.price * item.quantity).toLocaleString()}</span>
                  </div>
                ))}
              </div>

              <div className="order-total">
                <span>Total:</span>
                <span>${total.toLocaleString()}</span>
              </div>
            </div>

            <div className="checkout-actions">
              <button 
                onClick={() => setShowCheckout(false)} 
                className="back-button"
              >
                <FontAwesomeIcon icon={faArrowLeft} /> Back to Cart
              </button>
              {paymentMethod !== 'PAYPAL' && (
                <button 
                  onClick={handleCheckout} 
                  className="place-order-button"
                  disabled={processingOrder || !selectedAddressId}
                >
                  {processingOrder ? 'Processing...' : 'Place Order'}
                </button>
              )}
            </div>
          </div>
        ) : (
          <>
            <div className="cart-items">
              {cartItems.map(item => (
                <CartItem key={item.id} item={item} />
              ))}
            </div>

            <div className="cart-summary">
              <div className="select-all">
                <input
                  type="checkbox"
                  id="select-all"
                  checked={allSelected}
                  onChange={handleSelectAll}
                  className="cart-checkbox"
                />
                <label htmlFor="select-all" className="checkbox-label"></label>
                <span>Select All</span>
              </div>

              <div className="order-summary">
                <p className="summary-text">
                  Total ({selectedCount} {selectedCount === 1 ? 'item' : 'items'}):{" "}
                  <strong className="total-price">${total.toLocaleString()}</strong>
                </p>
                <button 
                  onClick={handleProceedToCheckout} 
                  className="checkout-btn"
                  disabled={selectedCount === 0 || processingOrder}
                >
                  {processingOrder ? 'Processing...' : 'Proceed to Checkout'}
                </button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default CartPage;
