import React, { useContext, useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faShoppingCart,
  faArrowLeft,
  faPlus,
  faMoneyBill,
  faCheck,
} from '@fortawesome/free-solid-svg-icons';
import { faCcPaypal } from '@fortawesome/free-brands-svg-icons';
import CartItem from '../common/CartItem';
import { CartContext } from '../../App';
import axios from 'axios';
import '../../styles/CartPage.css';
import '../../styles/PayPalButton.css';

const CartPage = () => {
  const { cartItems, setCartItems } = useContext(CartContext);
  const [processingOrder, setProcessingOrder] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [addresses, setAddresses] = useState([]);
  const [selectedAddressId, setSelectedAddressId] = useState('');
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('CASH_ON_DELIVERY');
  const [newAddress, setNewAddress] = useState({
    street: '',
    city: '',
    state: '',
    zipCode: '',
    country: '',
  });
  const [customerId, setCustomerId] = useState(null);
  const navigate = useNavigate();

  const fetchCartItems = useCallback(async () => {
    if (!customerId) return;
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:8080/api/cart/${customerId}`);
      const fetchedItems = response.data.map((item) => ({
        id: item.id,
        productId: item.product.id,
        name: item.product.name,
        size: item.size,
        price: item.product.price,
        quantity: item.quantity,
        image: item.product.image,
        selected: true,
      }));
      setCartItems(fetchedItems);
    } catch (error) {
      console.error('Error fetching cart items:', error);
      alert('Failed to load your cart. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  }, [customerId, setCartItems]);

  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      try {
        const userData = JSON.parse(storedUser);
        if (userData && userData.customerId) {
          setCustomerId(userData.customerId);
        } else {
          console.error('customerId not found in userData from localStorage');
        }
      } catch (error) {
        console.error('Error parsing currentUser from localStorage:', error);
      }
    } else {
      console.error('currentUser not found in localStorage');
    }
  }, []);

  useEffect(() => {
    if (customerId) {
      fetchCartItems();
    }
  }, [customerId, fetchCartItems]);

  const calculateTotal = () => {
    return cartItems
      .filter((item) => item.selected)
      .reduce((sum, item) => {
        const itemPrice = typeof item.price === 'string'
          ? parseInt(item.price.replace(/\D/g, ''))
          : item.price;
        return sum + item.quantity * itemPrice;
      }, 0);
  };

  const total = calculateTotal();

  const handleSelectAll = (e) => {
    const checked = e.target.checked;
    setCartItems((prev) => prev.map((item) => ({ ...item, selected: checked })));
  };

  const allSelected = cartItems.length > 0 && cartItems.every((item) => item.selected);
  const selectedCount = cartItems.filter((item) => item.selected).length;

  const fetchAddresses = useCallback(async () => {
    if (!customerId) return;
    try {
      const response = await axios.get(`http://localhost:8080/api/checkout/${customerId}/addresses`);
      setAddresses(response.data);
      if (response.data.length > 0 && !selectedAddressId) {
        setSelectedAddressId(response.data[0].id);
      }
    } catch (error) {
      console.error('Error fetching addresses:', error);
      alert('Failed to load addresses. Please try again.');
    }
  }, [customerId, selectedAddressId]);

  useEffect(() => {
    if (showCheckout && customerId) {
      fetchAddresses();
    }
  }, [showCheckout, customerId, fetchAddresses]);

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setNewAddress((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleAddAddress = async (e) => {
    e.preventDefault();
    try {
      setProcessingOrder(true);
      const response = await axios.post(
        `http://localhost:8080/api/checkout/${customerId}/address`,
        newAddress
      );
      setAddresses((prev) => [...prev, response.data]);
      setSelectedAddressId(response.data.id);
      setNewAddress({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: '',
      });
      setShowAddressForm(false);
    } catch (error) {
      console.error('Error adding address:', error);
      alert('Failed to add address. Please try again.');
    } finally {
      setProcessingOrder(false);
    }
  };

  const handleProceedToCheckout = () => {
    const selected = cartItems.filter((item) => item.selected);
    if (selected.length === 0) {
      alert("Please select products to checkout!");
    } else {
      setShowCheckout(true);
    }
  };

  const generateCODTransactionId = () => {
    const timestamp = new Date().getTime();
    const randomNum = Math.floor(Math.random() * 10000);
    return `COD_${timestamp}_${randomNum}`;
  };

  const handleCheckout = async () => {
    if (!selectedAddressId) {
      alert('Please select a shipping address');
      return;
    }

    setProcessingOrder(true);

    try {
      if (paymentMethod === 'PAYPAL') {
        setLoading(true);
        const response = await axios.post(
          `http://localhost:8080/api/paypal/create?customerId=${customerId}&addressId=${selectedAddressId}`
        );

        if (response.data && response.data.redirectUrl) {
          localStorage.setItem('paypalPaymentId', response.data.paymentId);
          window.location.href = response.data.redirectUrl;
          return;
        } else {
          throw new Error('Failed to initiate PayPal payment');
        }
      } else {
        const selectedItems = cartItems.filter((item) => item.selected);
        const orderRequest = {
          customerId,
          addressId: selectedAddressId,
          paymentMethod: 'CASH_ON_DELIVERY',
          transactionId: generateCODTransactionId(),
          cartItems: selectedItems.map((item) => ({
            id: item.productId,
            name: item.name,
            price: item.price,
            quantity: item.quantity,
            image: item.image,
            size: item.size,
          })),
        };

        const response = await axios.post('http://localhost:8080/api/orders/create', orderRequest);

        if (response.data) {
          setCartItems([]);
          navigate(`/payment-success?method=cod&orderId=${response.data.id}`);
        } else {
          throw new Error('Failed to create COD order');
        }
      }
    } catch (error) {
      console.error('Error during checkout:', error);
      const data = error.response?.data;
      const errorMessage = (data && typeof data === 'object' && data.message) ? data.message : data;
      alert(errorMessage || 'An error occurred during checkout.');
    } finally {
      setProcessingOrder(false);
      setLoading(false);
    }
  };

  return (
    <div className="cart-page-container">
      <div className="cart-container">
        <div className="cart-header">
          <h1>
            <FontAwesomeIcon icon={faShoppingCart} /> Shopping Cart
          </h1>
          <Link to="/" className="continue-shopping-link">
            <FontAwesomeIcon icon={faArrowLeft} /> Continue Shopping
          </Link>
        </div>

        {cartItems.length === 0 ? (
          <div className="empty-cart">
            <h2>Your cart is empty</h2>
            <p>Add some items to your cart to see them here.</p>
            <Link to="/" className="btn brown-bg">
              Start Shopping
            </Link>
          </div>
        ) : showCheckout ? (
          // Checkout UI
          <div className="checkout-container">
            {/* Add checkout content here */}
            <h2>Checkout</h2>
            {/* Address, Payment Method, Summary, etc. */}
            <button onClick={() => setShowCheckout(false)}>Back to Cart</button>
            <button onClick={handleCheckout} disabled={processingOrder || loading}>
              {processingOrder || loading ? 'Processing...' : 'Place Order'}
            </button>
          </div>
        ) : (
          <>
            {/* Cart Items List */}
            <div className="cart-items">
              {cartItems.map((item) => (
                <CartItem key={item.id} item={item} />
              ))}
            </div>

            {/* Cart Summary */}
            <div className="cart-summary">
              <div className="select-all">
                <input
                  type="checkbox"
                  id="select-all"
                  checked={allSelected}
                  onChange={handleSelectAll}
                  className="cart-checkbox"
                />
                <label htmlFor="select-all">Select All</label>
              </div>

              <div className="total-section">
                <span>Total: ${total.toLocaleString()}</span>
              </div>

              <button onClick={handleProceedToCheckout} className="proceed-checkout-btn">
                Proceed to Checkout
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default CartPage;
