import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faHome, faBox, faSignOutAlt, 
  faTachometerAlt, faPlusCircle 
} from '@fortawesome/free-solid-svg-icons';
import AdminProductList from './AdminProductList';
import AdminProductForm from './AdminProductForm';
import '../../styles/Admin.css';

const AdminDashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [adminUser, setAdminUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in as admin
    const storedUser = localStorage.getItem('adminUser');
    if (!storedUser) {
      navigate('/admin/login');
      return;
    }

    try {
      const user = JSON.parse(storedUser);
      if (!user.role) {
        // If no role or not admin role, redirect to login
        localStorage.removeItem('adminUser');
        navigate('/admin/login');
        return;
      }
      setAdminUser(user);
    } catch (error) {
      console.error('Error parsing admin user data:', error);
      localStorage.removeItem('adminUser');
      navigate('/admin/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('adminUser');
    navigate('/admin/login');
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'products':
        return <AdminProductList />;
      case 'add-product':
        return <AdminProductForm />;
      case 'dashboard':
      default:
        return (
          <div className="admin-welcome">
            <h2>Welcome to Admin Dashboard</h2>
            <p>Select an option from the sidebar to manage your store.</p>
            <div className="admin-stats">
              <div className="stat-card">
                <h3>Products</h3>
                <p>Manage your product inventory</p>
                <button 
                  className="admin-action-btn"
                  onClick={() => setActiveSection('products')}
                >
                  <FontAwesomeIcon icon={faBox} /> View Products
                </button>
              </div>
              <div className="stat-card">
                <h3>New Product</h3>
                <p>Add a new product to your store</p>
                <button 
                  className="admin-action-btn"
                  onClick={() => setActiveSection('add-product')}
                >
                  <FontAwesomeIcon icon={faPlusCircle} /> Add Product
                </button>
              </div>
            </div>
          </div>
        );
    }
  };

  if (!adminUser) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="admin-container">
      <div className="admin-sidebar">
        <div className="admin-sidebar-header">
          <h3>Admin Panel</h3>
          <p>{adminUser.username}</p>
        </div>
        <ul className="admin-menu">
          <li 
            className={activeSection === 'dashboard' ? 'active' : ''}
            onClick={() => setActiveSection('dashboard')}
          >
            <FontAwesomeIcon icon={faTachometerAlt} /> Dashboard
          </li>
          <li 
            className={activeSection === 'products' ? 'active' : ''}
            onClick={() => setActiveSection('products')}
          >
            <FontAwesomeIcon icon={faBox} /> Products
          </li>
          <li 
            className={activeSection === 'add-product' ? 'active' : ''}
            onClick={() => setActiveSection('add-product')}
          >
            <FontAwesomeIcon icon={faPlusCircle} /> Add Product
          </li>
          <li onClick={() => navigate('/')}>
            <FontAwesomeIcon icon={faHome} /> Go to Site
          </li>
          <li onClick={handleLogout}>
            <FontAwesomeIcon icon={faSignOutAlt} /> Logout
          </li>
        </ul>
      </div>
      <div className="admin-content">
        {renderContent()}
      </div>
    </div>
  );
};

export default AdminDashboard;
