import React, { useContext, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPlus, faMinus } from '@fortawesome/free-solid-svg-icons';
import { CartContext } from '../../App';
import '../../styles/CartItem.css';
import '../../styles/SizeModal.css';

const CartItem = ({ item }) => {
  const { updateQuantity, removeFromCart, toggleItemSelection } = useContext(CartContext);
  const [isSizeModalOpen, setIsSizeModalOpen] = useState(false);
  const [newSelectedSize, setNewSelectedSize] = useState(item.size);

  // This will be replaced with a fetch call later
  const availableSizes = ['S', 'M', 'L', 'XL', '2XL'];

  const handleOpenSizeModal = () => {
    setNewSelectedSize(item.size);
    setIsSizeModalOpen(true);
  };

  const handleCloseSizeModal = () => {
    setIsSizeModalOpen(false);
  };

  const handleConfirmSizeChange = () => {
    if (newSelectedSize !== item.size) {
      console.log(`Updating item ${item.id} to size ${newSelectedSize}`);
      // updateItemSize(item.id, newSelectedSize); // This will be implemented later
    }
    handleCloseSizeModal();
  };

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity < 1) return;
    updateQuantity(item.id, newQuantity);
  };

  const product = item.product || {};
  const productName = item.name || product.name || 'Product';
  const productPrice = item.price || product.price || 0;
  const productImage = item.image || product.image || '/images/placeholder.png';

  const formatPrice = (price) => {
    const numPrice = typeof price === 'string' ? parseFloat(price.replace(/,/g, '')) : price;
    return numPrice.toLocaleString();
  };

  const itemTotal = item.quantity * (typeof productPrice === 'string' ? parseFloat(productPrice.replace(/,/g, '')) : productPrice);

  return (
    <>
      <div className="cart-item-card">
        <div className="item-selection">
          <input 
            type="checkbox" 
            checked={item.selected} 
            onChange={() => toggleItemSelection(item.id)} 
            className="item-checkbox"
          />
        </div>

        <img src={productImage} alt={productName} className="cart-item-image" />

        <div className="cart-item-details">
          <h3 className="product-name">{productName}</h3>
          {item.size && (
            <div className="item-size-container">
              <p className="item-size">Size: {item.size}</p>
              <button className="change-size-btn" onClick={handleOpenSizeModal}>Change</button>
            </div>
          )}
          <p className="product-price">₫{formatPrice(productPrice)}</p>
        </div>

        <div className="item-quantity-selector">
          <button onClick={() => handleQuantityChange(item.quantity - 1)} className="quantity-btn">
            <FontAwesomeIcon icon={faMinus} />
          </button>
          <span className="item-quantity">{item.quantity}</span>
          <button onClick={() => handleQuantityChange(item.quantity + 1)} className="quantity-btn">
            <FontAwesomeIcon icon={faPlus} />
          </button>
        </div>

        <div className="item-total-price">
          ₫{formatPrice(itemTotal)}
        </div>

        <button onClick={() => removeFromCart(item.id)} className="item-remove-btn">
          <FontAwesomeIcon icon={faTrash} />
        </button>  
      </div>

      {isSizeModalOpen && (
        <SizeModal 
          availableSizes={availableSizes}
          selectedSize={newSelectedSize}
          onSelectSize={setNewSelectedSize}
          onClose={handleCloseSizeModal}
          onConfirm={handleConfirmSizeChange}
        />
      )}
    </>
  );
};

export default CartItem;

const SizeModal = ({ availableSizes, selectedSize, onSelectSize, onClose, onConfirm }) => (
  <div className="size-modal-overlay" onClick={onClose}>
    <div className="size-modal-content" onClick={(e) => e.stopPropagation()}>
      <p className="size-modal-title">Size:</p>
      <div className="size-options">
        {availableSizes.map(size => (
          <button
            key={size}
            className={`size-option-btn ${selectedSize === size ? 'selected' : ''}`}
            onClick={() => onSelectSize(size)}
          >
            {size}
            {selectedSize === size && <div className="selected-tick">✔</div>}
          </button>
        ))}
      </div>
      <div className="size-modal-actions">
        <button className="modal-cancel-btn" onClick={onClose}>CANCEL</button>
        <button className="modal-confirm-btn" onClick={onConfirm}>CONFIRM</button>
      </div>
    </div>
  </div>
);
